main.script:
$(document).ready(function () {
    var current = location.pathname;
    $('#sidebar a').each(function () {
        var $this = $(this);
        // if the current path is like this link, make it active
        if ($this.attr('href').indexOf(current) !== -1) {
            $this.parent().addClass('active');
        }
    })
});

