                                        function NewsDetail(id) {
                                            $.ajax({
                                                url: '/Home/GetNews',
                                                data: { 'id': id },
                                                type: 'GET',
                                                dataType: 'JSON',
                                                success: function (data) {
                                                    var DataID = data.DataID;
                                                    $("#lblCaption").html(data.DataCaption);
                                                    $("#lblDescription").html(data.DataDescription);
                                                    var DataText = data.DataText;
                                                    var pieces = DecodeJsonDate(data.Date).split('/');
                                                    $("#lblDate").html(pieces.join('-'));
                                                    $('#myModal').modal('show');
                                                }
                                            });
                                        }

