$(document).ready(function () {
    // Add minus icon for collapse element which is open by default
    $(".collapse.show").each(function () {
        $(this).prev(".card-header").find(".fa").addClass("fa-minus").removeClass("fa-plus");
    });

    // Toggle plus minus icon on show hide of collapse element
    $(".collapse").on('show.bs.collapse', function () {
        $(this).prev(".card-header").find(".fa").removeClass("fa-plus").addClass("fa-minus");
    }).on('hide.bs.collapse', function () {
        $(this).prev(".card-header").find(".fa").removeClass("fa-minus").addClass("fa-plus");
    });
});

                                        function LoadDatas(MasterID, Type, Title) {
                                            $("#lblCaption").html(Title);
                                            if (Type == "D") {
                                                $.ajax({
                                                    url: '/Home/GetDataByMasterID',
                                                    data: { 'id': MasterID },
                                                    type: 'GET',
                                                    dataType: 'JSON',
                                                    success: function (data) {
                                                        $("#AcdemicsModal #lblDescription").html("");
                                                        $("#AcdemicsModal #FilesDivShow").html("");
                                                        $("#AcdemicsModal #lblDescription").html(data.DataDescription);
                                                        $('#AcdemicsModal').modal('show');
                                                    }
                                                });
                                            } else {
                                                $.ajax({
                                                    url: '/Home/GetFilesAndImagesByMaster',
                                                    data: { 'id': MasterID },
                                                    type: 'GET',
                                                    dataType: 'JSON',
                                                    success: function (data) {
                                                        $("#AcdemicsModal #lblDescription").html("");
                                                        $("#AcdemicsModal #FilesDivShow").html("");
                                                        $.each(data, function (j, val) {
                                                            if (val.FileName.split('.').pop() != "jpeg") {
                                                                $("#AcdemicsModal #FilesDivShow").append(
                                                                '

' +
                                                                '' +
                                                                '
' + val.FileCaption + '' +
                                                                'View' +
                                                                ''
                                                                    );
                                                            }
                                                        });
                                                        $('#AcdemicsModal').modal('show');
                                                    }
                                                });
                                            }
                                        }

