function AboutUsDetail(id) {
    var Description = "";
    switch (id) {
        case 'Profile':
            $("#lblDescription").html($("#Profile").html());
            break;
        case 'Vision':
            $("#lblDescription").html($("#Vision").html());
            break;
        case 'Mission':
            $("#lblDescription").html($("#Mission").html());
            break;
    }
    $("#lblCaption").html(id);
    //$("#lblDescription").html(Description);
    $('#myModal').modal('show');
}

