main.script:
function ShowDescDetail(Caption) {
    $("#lblCaption").html(Caption);
    switch (Caption) {
        case 'Welcome To EMEA':
            $("#lblDescription").html($("#welMessege").html());
            break;
        case 'Principal Message':
            $("#lblDescription").html($("#PrincMessege").html());
            $("#lblCaption").html("Principal's Message");
            break;
    }

    $('#myDescModal').modal('show');
}

   function NewsDetail(id) {
       $.ajax({
           url: '/Home/GetNews',
           data: { 'id': id },
           type: 'GET',
           dataType: 'JSON',
           success: function (data) {
           var DataID = data.DataID;
           $("#ActivityModal #lblCaption").html(data.DataCaption);
           $("#ActivityModal #lblDescription").html(data.DataDescription);
           var DataText = data.DataText;
           var pieces = DecodeJsonDate(data.Date).split('/');
           $("#ActivityModal #lblDate").html(pieces.join('-'));
           $('#ActivityModal').modal('show');
           }
           });
}
